(defn puzzle1
  [input]
  (let [length (count input)]
    (loop [n 0
           instructions input
           iterations 0]
      (if (or (> 0 n)
              (<= length n))
        iterations
        (recur (+ n (nth instructions n))
               (update instructions n inc)
               (inc iterations))))))

(defn puzzle2-update-fn
  [x]
  (if (< x 3)
    (inc x)
    (dec x)))

(defn puzzle2
  [input]
  (let [length (count input)]
    (loop [n 0
           instructions input
           iterations 0]
      (if (or (> 0 n)
              (<= length n))
        iterations
        (recur (+ n (nth instructions n))
               (update instructions n puzzle2-update-fn)
               (inc iterations))))))
